import React, { useState } from 'react'
import MaterialModal from '../modal'

class StudentForm extends Component {
  constructor() {
    super();
    this.state = {
      errors: []
    };
  }

  handleSubmit = e => {
    e.preventDefault();

    const name = ReactDOM.findDOMNode(this._nameInput).value;
    const email = ReactDOM.findDOMNode(this._emailInput).value;
    const password = ReactDOM.findDOMNode(this._passwordInput).value;

    const errors = validate(name, email, password);
    if (errors.length > 0) {
      this.setState({ errors });
      return;
    }

    // submit the data...
        props.setStudents(values, props.id)
        props.setIsOpen(false)
  };


  render() {
    const { errors } = this.state;
    return (
      <MaterialModal>
        <form onSubmit={this.handleSubmit}>
          {errors.map(error => (
            <p key={error}>Error: {error}</p>
          ))}
          <input
            ref={nameInput => this._nameInput}
            type="text"
            placeholder="Name"
          />
          <input
            ref={emailInput => this._emailInput}
            type="text"
            placeholder="Email"
          />
          <input
            ref={passwordInput => this._passwordInput}
            type="password"
            placeholder="Password"
          />

          <button type="submit">Submit</button>
        </form>
      </MaterialModal>
    );
  }
}
