import React, { useState } from 'react'
import MaterialModal from '../modal'


const StudentForm = (props) => {

  state = {
    errors: []
  }

  const [values, setValue] = useState({})

  const handleChange = (key, val) => {
    setValue(prevState => ({ ...prevState, [key]: val }))
  }

  const handleSubmit = () => {
    handleSubmit = e => {
      e.preventDefault();

      const name = ReactDOM.findDOMNode(this._nameInput).value;
      const email = ReactDOM.findDOMNode(this._emailInput).value;
      const password = ReactDOM.findDOMNode(this._passwordInput).value;

      const errors = validate(name, email, password);
      if (errors.length > 0) {
        this.setState({ errors });
        return;
      }

      // submit the data...
      props.setStudents(values, props.id)
      props.setIsOpen(false)
    };
    // props.setStudents(values, props.id)
    // props.setIsOpen(false)
  }

  const { errors } = this.state;
  return (
    <MaterialModal setIsOpen={props.setIsOpen}>
      <form className="form-style-9" onSubmit={this.handleSubmit} >
      {errors.map(error => (
          <p key={error}>Error: {error}</p>
        ))}
        <h3>New Student</h3>
            <input
              ref={nameInput => this._nameInput}
              type="text" name="fullName" required
              className="field-style field-full align-none"
              placeholder="Full Name..."
              onChange={e => handleChange(e.target.name, e.target.value)}/>
            <input
              type="email" name="email" id="emailField" required
              className="field-style field-full align-none"
              placeholder="Email..."
              onChange={e => handleChange(e.target.name, e.target.value)}/>
            <input
              type="number" min="6" max="19" id="ageField" required
              name="age" className="field-style field-full align-none"
              placeholder="Student's Age..."
              onChange={e => handleChange(e.target.name, e.target.value)}/>
            <button onClick={() => handleSubmit()} className="submit" value="Submit" >Submit</button>
      </form>
    </MaterialModal>

  )
}


export default StudentForm
